import sqlite3
from sqlite3 import Error


def create_connection(chemistry_db_file):
    """ create a database connection to the SQLite database
        specified by chemistry_db_file
    :param chemistry_db_file: database file
    :return: Connection object or None
    """
    conn = None
    try:
        conn = sqlite3.connect(chemistry_db_file)
    except Error as e:
        print(e)

    return conn


def create_element(conn, element):
    """
    Create a new element into the element table
    :param conn:
    :param element:
    :return: element id
    """
    sql = ''' INSERT INTO element(symbol,name,atomic_number,mass,period,row,column,_group,protons,neutrons,
        electrons,_1s,_2s,_2p,_3s,_3p,_4s,_3d,_4p,_4d,_5s,_5p,_6s,_5d,_6p,_7s,affinity,density,electronegativity,
        melt,boil,e_fusion,e_vapor,t_crit,p_crit,valence,a_radius)
         VALUES(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?) '''

    cur = conn.cursor()
    cur.execute(sql, element)
    conn.commit()
    return cur.lastrowid

def insert_element(conn, element):
    """
    Create a new element into the element table
    :param conn:
    :param element:
    :return: element id
    """

    sql = ''' INSERT INTO element(symbol,name,atomic_number,mass,period,row,column,_group,protons,neutrons,electrons,_1s,_2s,_2p,_3s,_3p,_4s,_3d,_4p,_4d,_5s,_5p,_6s,_5d,_6p,_7s,affinity,density,electronegativity,melt,boil,e_fusion,e_vapor,t_crit,p_crit,valence,a_radius)
              VALUES(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?) '''
    cur = conn.cursor()
    cur.execute(sql, element)
    conn.commit()
    return cur.lastrowid


